import {useRouter} from 'next/router';
import Layout from '../comps/Layout';

const RecipeIndex = () => {
    const router = useRouter();

    return(
      <Layout>
        <h1>{router.query.title}</h1>
        <div className="row">
          <div className="col">
            <h1>Recipes</h1>
          </div>
          <div className="col-lg-3">
            <div className="recipe-card">
              {/* <img className="img-fluid" src={props.data.image} alt={props.data.title} />
              <h2>{props.data.title}</h2> */}
              <div className="recipe-card__meta">
                <ul>
                  {/* <li>{props.data.readyInMinutes} min</li>
                  <li>{props.data.servings} people</li> */}
                </ul>
              </div>
            </div>
          </div>
        </div>
    </Layout>
    )
}

RecipeIndex.getInitialProps = async function(context){
  const {query} = context.query.q;
  const queryPath = context.asPath;
  //clean up queryPath to match better what the api expects
  const replaceChar = {
    "%2C": ",",
    "%20": "+"
  }

  function cleanPath(){
    let cleanedPath = queryPath.replace(/%2C|%20/gi, function(matched){
      return replaceChar[matched];
    })
  }

  const cleanedPath = cleanPath;

  console.log(queryPath, cleanedPath);
  //console.log(query, context.pathname, context.asPath, context.res); undefined, /recipes, /recipes?q=flour, undefined
  const response = await fetch(`https://api.spoonacular.com/recipes/findByIngredients?ingredients=${query}&apiKey=${process.env.API_KEY}`);
  const data = await response.json();

  // console.log(transformQuery);

  return {
      recipes: data
  };
};

export default RecipeIndex;
