import Layout from '../comps/Layout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const labelStyle = {
    color: '#764248',
    fontWeight: 'bold',
    textTransform: 'uppercase',
    fontSize : '1.200rem'
}

var path = 'https://api.spoonacular.com/recipes/findByIngredients?ingredients=apples,+flour,+sugar&number=2&apiKey=';
var apiKey = "e4dae24ad2ad4d6cb33e377041aa1e0b";

var fullPath = path + apiKey;

const PostLink = props => (
    <li>
        <Link href="/p/[id]" as={`/p/${props.id}`}>
          <a>{props.id}</a>
        </Link>
    </li>
)

class searchForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            value: '',
        }
        this.handleChange = this.handleChange.bing(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){
        console.log(this.state.value);
        event.preventDefault();
    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                    <label for="ingredient">
                        Ingredients
                    </label>
                    <input type="text" name="ingredient"/>
                </div>
                <button type="submit" value="Submit">Submit</button>
            </form>
        )
    }
}

const Index = props => (
    <Layout>

      <h1>API Working?</h1>
      <ul>
        {props.recipes.map(recipe => (
          <li key={recipe.id}>
            <Link href="/p/[id]" as={`/p/${recipe.id}`}>
              <a>{recipe.title}</a>
            </Link>
          </li>
        ))}
      </ul>
    </Layout>
  );

  Index.getInitialProps = async function(){
    const response = await fetch(fullPath);
    const data = await response.json();

    return {
        recipes: data
    };
};

export default Index;
