import {useRouter} from 'next/router';
import Layout from '../comps/Layout';

// var path = 'https://api.spoonacular.com/recipes/findByIngredients?ingredients=apples,+flour,+sugar&apiKey=';

const RecipeIndex = () => {
    const router = useRouter();

    return(
        <Layout>
            <h1>{router.query.title}</h1>
    <div className="row">
      <div className="col">
        <h1>Recipes</h1>
      </div>
      <div className="col-lg-3">
        <div className="recipe-card">
          {/* <img className="img-fluid" src={props.data.image} alt={props.data.title} />
          <h2>{props.data.title}</h2> */}
          <div className="recipe-card__meta">
            <ul>
              {/* <li>{props.data.readyInMinutes} min</li>
              <li>{props.data.servings} people</li> */}
            </ul>
          </div>
        </div>
      </div>
    </div>
  </Layout>
    )
}

RecipeIndex.getInitialProps = async function(){

  const response = await fetch("https://api.spoonacular.com/recipes/findByIngredients?ingredients=apples,+flour,+sugar&apiKey=e4dae24ad2ad4d6cb33e377041aa1e0b");
  const data = await response.json();

  console.log(router.query);

  return {
      recipes: data
  };
};

export default RecipeIndex;
